import React from "react";
import {Button} from "react"

class Counter extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        value: 0,
      };
    }

    addValue = (v) => {
        this.setState({
            value: this.value+1,
        });
    }

    render() {
      return (
        <>
        <h1>{this.state.value}</h1>

        </>
      );
    }
  }
  export default Counter;
