import React from "react";

class Counter extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        value: props.value,
      };
    }

    addValue = (v) => {
        this.setState({
            value: this.value+v,
        });
    }

    render() {
      return (
        <div>
            <button onClick={() => this.addValue(1)}>Clique para incrementar</button>
            <h1>{this.state.value}</h1>
            <button onClick={() => this.addValue(-1)}>Clique para decrementar</button>
        </div>
      );
    }
  }
  export default Counter;
