import {React, Button} from "react";

class Counter extends React.Component {

    constructor(props) {
      super(props);

      this.state = {
        value: props.value,
      };
    }

    addValue = (v) => {
        this.setState({
            value: this.value+1,
        });
    }

    render() {
      return (
        <>
            <h1>{this.state.value}</h1>
            <Button onClick={() => { alert('clicked'); }}> + </Button>
        </>
      );
    }
  }
  export default Counter;
