import {React, Button, View} from "react";

class Counter extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        value: 0,
      };
    }

    addValue = (v) => {
        this.setState({
            value: this.value+1,
        });
    }

    render() {
      return (
        <View>
            <h1>{this.state.value}</h1>
            <Button onClick={() => { alert('clicked'); }}> + </Button>
        </View>
      );
    }
  }
  export default Counter;
